package javaclasses;

public class ConditionIfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a =90;

if (a>100) {
System.out.println(" Value is more than 100 -  " + a);
System.out.println(" checking second code ");
	}
else if (a==100) {
	System.out.println(" Value is exactly 100 -  " + a);
	System.out.println(" checking second code ");
	System.out.println(" checking second code ");

}
else 
	System.out.println(" Value is less than 100 -  " + a);
	System.out.println(" checking second code ");
	System.out.println(" checking second code ");
	System.out.println(" checking second code ");
}
}