package javaclasses;

public class LoopClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=1;i<=100;i=i+1) {
System.out.print(i+",");
	
}

int i=10;

while(i>5) {
	
	if(i==10)
	
	{
			System.out.println();
	}
	System.out.println("While loop " + i); 
	i--;

	}
	}
}